s = list(map(int, input('One line python3 Hamming code decoder by @nime_boy\n> '))); ans = sum([2 ** j for j in range(0, int.bit_length(len(s))) if sum([x for xs in [s[2 ** j - 1 + 2 ** j * i : 2 ** j - 1 + 2 ** j * (i + 1)] for i in range(len(s) // 2 ** j)][::2] for x in xs]) % 2 == 1]); s[ans - 1] = s[ans - 1] ^ 1 if ans != 0 else s[ans - 1]; print("Wrong bit:", ans, '\nDecoded string:', ''.join(list(map(str, [s[i - 1] for i in range(1, len(s) + 1) if int.bit_length(i) == int.bit_length(i - 1)]))))